import styled from "styled-components"

export const Card = styled.div`
    border-width: 20px;
    border-style: double;
    border-color: #030;
    opacity: 0.8;
    margin-top:200px;

    display:flex;
    justify-content: center;
    color:white;
    height:100px;
    `

export const Header = styled.div`
    width:100%;
    background: no-repeat url(https://blog.shoppub.com.br/wp-content/uploads/2018/03/Pixabay.jpg);

    display:flex;
    flex-direction: column;
    justify-content: center;
    `
